const harryCursor=document.querySelector(".harry-cursor");var lockCursor=!1;function showCursor(){lockCursor||harryCursor.classList.remove("harry-cursor-hide")}function hideCursor(){harryCursor.classList.add("harry-cursor-hide")}window.addEventListener("mousemove",a=>{harryCursor.style.top=a.clientY+"px",harryCursor.style.left=a.clientX+"px",harryCursor.style.visibility="visible",showCursor()}),window.addEventListener("mousedown",()=>{hideCursor(),lockCursor=!0}),window.addEventListener("mouseup",()=>{lockCursor=!1,showCursor()}),window.addEventListener("mouseout",()=>{hideCursor()}),$("body").on("mouseenter","a,button",()=>{hideCursor(),lockCursor=!0}).on("mouseleave","a,button",()=>{lockCursor=!1,showCursor()})